function swapApolloProvider() {
  return {
    name: "redwood-swap-apollo-provider",
    async transform(code, id) {
      if (/web\/src\/App\.(ts|tsx|js|jsx)$/.test(id)) {
        return code.replace(
          "@redwoodjs/web/apollo",
          "@redwoodjs/web/dist/apollo/suspense"
        );
      }
      return code;
    }
  };
}
export {
  swapApolloProvider
};
